import { NavLink } from 'react-router-dom';
import Profile from '../Images/profile.png';
import GitHubIcon from '@mui/icons-material/GitHub';
import LinkedInIcon from '@mui/icons-material/LinkedIn';
import TwitterIcon from '@mui/icons-material/Twitter';
import EmailIcon from '@mui/icons-material/Email';
import HomeIcon from '@mui/icons-material/Home';
import FolderSpecialIcon from '@mui/icons-material/FolderSpecial';
import AutoStoriesIcon from '@mui/icons-material/AutoStories';
import InfoIcon from '@mui/icons-material/Info';
import ContactsIcon from '@mui/icons-material/Contacts';
import MenuIcon from '@mui/icons-material/Menu';
import { useState, useEffect } from 'react';

const Navbar = () => {
    const [showMenu, setShowMenu] = useState('block');

    let deviceWidth = window.innerWidth;

    useEffect(() => {
        if (deviceWidth < 800) {
            setShowMenu('none')
        }
    })

    const showNavItems = () => {
        setShowMenu('block')
    }

    return (
        <>
            <div className="details">
                <div className="mobile-menu" onClick={showNavItems}>
                    <MenuIcon className='icon' />
                </div>
                <div className="profile">
                    <img src={Profile} alt="profile" />
                </div>
                <div className="myName">
                    <h2>Niket Kumar Mishra</h2>
                    <h4>Student | Developer</h4>
                </div>
                <div className="social">
                    <a href="">
                        <GitHubIcon />
                    </a>
                    <a href="">
                        <LinkedInIcon />
                    </a>
                    <a href="">
                        <TwitterIcon />
                    </a>
                    <a href="">
                        <EmailIcon />
                    </a>
                </div>
            </div>
            <div
                className='nav-items'
                style={{ display: showMenu }}
            >
                <ul>
                    <NavLink
                        to='/'
                        className={({ isActive }) => (isActive ? 'active' : 'inActive')}
                    >
                        <li><HomeIcon />Home</li>
                    </NavLink>
                    <NavLink
                        to='/projects'
                        className={({ isActive }) => (isActive ? 'active' : 'inActive')}
                    >
                        <li><FolderSpecialIcon />Projects</li>
                    </NavLink>
                    <NavLink
                        to='/blogs'
                        className={({ isActive }) => (isActive ? 'active' : 'inActive')}
                    >
                        <li><AutoStoriesIcon />Blogs</li>
                    </NavLink>
                    <NavLink
                        to='/about'
                        className={({ isActive }) => (isActive ? 'active' : 'inActive')}
                    >
                        <li><InfoIcon />About</li>
                    </NavLink>
                    <NavLink
                        to='/contact'
                        className={({ isActive }) => (isActive ? 'active' : 'inActive')}
                    >
                        <li><ContactsIcon />Contact</li>
                    </NavLink>
                </ul>
            </div>
        </>
    );
}

export default Navbar;
